package Casting;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;
	    System.out.println("The integer value: " + num);

	    // convert into double type
	    double data = num;
	    System.out.println("The double value: " + data);
	  

	}

}
